import React from 'react';
import { useNavigate } from 'react-router-dom';
import './TouristPlacesPage.css'; // Import your CSS file

const samplePlaces = [
  {
    id: 1,
    name: 'Sigiriya',
    location: 'Sri Lanka',
    desc: 'Ancient rock fortress',
    img: '/images/sigiriya.jpg', // example path
  },
  {
    id: 2,
    name: 'Ella',
    location: 'Sri Lanka',
    desc: 'Scenic hill country',
    img: '/images/ella.jpg',
  },
  {
    id: 3,
    name: 'Galle',
    location: 'Sri Lanka',
    desc: 'Dutch fort city',
    img: '/images/galle.jpg',
  },
  {
    id: 4,
    name: 'Nuwara Eliya',
    location: 'Sri Lanka',
    desc: 'Tea plantations',
    img: '/images/nuwaraeliya.jpg',
  },
  // Add more places if needed
];

export default function TouristPlacesPage() {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user') || 'null');

  if (!user) {
    navigate('/');
    return null;
  }

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/');
  };

  return (
    <div className="places-container">
      <div className="places-header">
        <h2>Welcome, {user.name}</h2>
        <button className="btn" onClick={handleLogout}>Logout</button>
      </div>

      <div className="places-grid">
        {samplePlaces.map((p) => (
          <div className="place-card" key={p.id}>
            <img src={p.img} alt={p.name} className="place-image" />
            <h3>{p.name}</h3>
            <p className="place-location">{p.location}</p>
            <p className="place-desc">{p.desc}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
