// src/pages/RegisterPage.jsx
import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../styles/RegisterPage.css";

export default function RegisterPage() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirm, setConfirm] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleRegister = async (e) => {
    e.preventDefault();
    setError("");
    if (!name || !email || !password || !confirm) {
      setError("Please fill all fields.");
      return;
    }
    if (password !== confirm) {
      setError("Passwords do not match.");
      return;
    }

    try {
      setLoading(true);
      // change '/api/register' if your backend route is different
      await axios.post("http://localhost:5000/api/register", {
        name,
        email,
        password,
      });
      // go to login page after successful registration
      navigate("/client/src/pages/LoginPage.js"); // <-- adjust route if your login route is different
    } catch (err) {
      setError(err.response?.data?.message || "Registration failed.");
    } finally {
      setLoading(false);
    }
  };

  const handleCancel = () => {
    // clear fields and go back to login/home
    setName("");
    setEmail("");
    setPassword("");
    setConfirm("");
    setError("");
    navigate("/client/src/pages/LoginPage.js"); // adjust as needed
  };

  return (
    <div className="register-viewport">
      <div className="register-card" role="region" aria-label="Create account">
        <h2 className="register-title">Create Account</h2>

        <form onSubmit={handleRegister} className="register-form">
          <label className="sr-only" htmlFor="name">Name</label>
          <input
            id="name"
            className="input"
            placeholder="Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            autoComplete="name"
          />

          <label className="sr-only" htmlFor="email">Email</label>
          <input
            id="email"
            className="input"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            autoComplete="email"
            type="email"
          />

          <label className="sr-only" htmlFor="password">Password</label>
          <input
            id="password"
            type="password"
            className="input"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            autoComplete="new-password"
          />

          <label className="sr-only" htmlFor="confirm">Confirm Password</label>
          <input
            id="confirm"
            type="password"
            className="input"
            placeholder="Confirm Password"
            value={confirm}
            onChange={(e) => setConfirm(e.target.value)}
            autoComplete="new-password"
          />

          {error && <div className="error">{error}</div>}

          <div className="button-row">
            <button className="btn primary" type="submit" disabled={loading}>
              {loading ? "Creating..." : "Register"}
            </button>
            <button
              type="button"
              className="btn secondary"
              onClick={handleCancel}
              disabled={loading}
            >
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
