import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../styles/RegisterPage.css";

export default function RegisterPage() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirm, setConfirm] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);
  const [isRightPanel, setIsRightPanel] = useState(true); // handles panel animation

  const navigate = useNavigate();

  const handleRegister = async (e) => {
    e.preventDefault();
    setError("");

    if (!name || !email || !password || !confirm) {
      setError("Please fill all fields.");
      return;
    }
    if (password !== confirm) {
      setError("Passwords do not match.");
      return;
    }

    try {
      setLoading(true);
      await axios.post("http://localhost:5000/api/register", {
        name,
        email,
        password,
      });

      setShowSuccess(true);
      setTimeout(() => {
        setShowSuccess(false);
        navigate("/login");
      }, 1500);
    } catch (err) {
      setError(err.response?.data?.message || "Registration failed.");
    } finally {
      setLoading(false);
    }
  };

  const handleCancel = () => {
    setName("");
    setEmail("");
    setPassword("");
    setConfirm("");
    setError("");
    setIsRightPanel(false); // slide back to sign-in panel
  };

  return (
    <div className={`container ${isRightPanel ? "right-panel-active" : ""}`}>
      {/* SIGN IN PANEL */}
      <div className="form-container sign-in-container">
        <div className="form-content">
          <h2>Welcome Back!</h2>
          <p>To keep connected with us, please login with your personal info</p>
          <button className="ghost" onClick={() => navigate("/login")}>Sign In</button>
        </div>
      </div>

      {/* SIGN UP PANEL */}
      <div className="form-container sign-up-container">
        <form onSubmit={handleRegister}>
          <h2>Create Account</h2>

          <div className="social-container">
            <button className="social">f</button>
            <button className="social">G+</button>
            <button className="social">in</button>
          </div>
          <span>or use your email for registration:</span>

          <input
            type="text"
            placeholder="Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            style={{ color: "black" }}
          />
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            style={{ color: "black" }}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            style={{ color: "black" }}
          />
          <input
            type="password"
            placeholder="Confirm Password"
            value={confirm}
            onChange={(e) => setConfirm(e.target.value)}
            style={{ color: "black" }}
          />

          {error && <div className="error">{error}</div>}

          <button type="submit" className="btn" disabled={loading || showSuccess}>
            {loading ? "Creating..." : "Sign Up"}
          </button>
          <button type="button" className="btn cancel" onClick={handleCancel}>
            Cancel
          </button>
        </form>
      </div>

      {/* OVERLAY PANEL FOR ANIMATION */}
      <div className="overlay-container">
        <div className="overlay">
          <div className="overlay-panel overlay-right">
            <h2>Hello, Friend!</h2>
            <p>Enter your personal details and start your journey with us</p>
            <button className="ghost" onClick={() => setIsRightPanel(true)}>
              Create Account
            </button>
          </div>
        </div>
      </div>

      {showSuccess && (
        <div className="success-modal">
          <div className="success-box">
            <div className="loader" />
            <div className="success-text">Account created successfully!</div>
          </div>
        </div>
      )}
    </div>
  );
}
