import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

export default function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/login', { email, password });
      // store user in localStorage (simple)
      localStorage.setItem('user', JSON.stringify(res.data.user));
      navigate('/places');
    } catch (err) {
      setError(err.response?.data?.message || 'Login failed');
    }
  };

  return (
    <div className="app-container">
      <div className="navbar">
        <div className="logo">Globe Hub</div>
        <div>
          <Link to="/register" style={{color:'#ff7a00', textDecoration:'none'}}>Sign up</Link>
        </div>
      </div>
      <div className="container">
        <div className="left">
          <h1 style={{fontSize:64}}>BEST PLACE <span style={{color:'#ff7a00'}}>TO FIND</span></h1>
          <p style={{marginTop:20}}>AND EXPLORE<br/>THAT ALL YOU NEED</p>
          <button className="btn" style={{width:160, marginTop:20}}>JOIN US</button>
        </div>
        <div className="right">
          <div className="form-card">
            <h2 style={{textAlign:'center', color:'#ff7a00'}}>Login Here</h2>
            <form onSubmit={handleLogin}>
              <input className="input" placeholder="Enter Email Here" value={email} onChange={e=>setEmail(e.target.value)} />
              <input type="password" className="input" placeholder="Enter Password Here" value={password} onChange={e=>setPassword(e.target.value)} />
              {error && <div style={{color:'#ff7676'}}>{error}</div>}
              <button className="btn" type="submit">Login</button>
            </form>
            <div style={{textAlign:'center', marginTop:12, color:'#fff'}}>Don't have an account? <Link to="/register" style={{color:'#ff7a00'}}>Sign up here</Link></div>
          </div>
        </div>
      </div>
    </div>
  );
}
