import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/TouristPlacesPage.css';

const samplePlaces = [
  {
    id: 1,
    name: 'Sigiriya',
    location: 'Sri Lanka',
    desc: 'Ancient rock fortress',
    img: '/images/sigiria.jpg',
  },
  {
    id: 2,
    name: 'Ella',
    location: 'Sri Lanka',
    desc: 'Scenic hill country',
    img: '/images/ella.jpg',
  },
  {
    id: 3,
    name: 'Galle',
    location: 'Sri Lanka',
    desc: 'Dutch fort city',
    img: '/images/galle-fort-1050x700-1.jpg',
  },
  {
    id: 4,
    name: 'Nuwara Eliya',
    location: 'Sri Lanka',
    desc: 'Tea plantations and cool weather',
    img: '/images/nuwara-eliya-tea-plantations-visit.jpg',
  },
  {
    id: 5,
    name: 'Kandy',
    location: 'Sri Lanka',
    desc: 'Temple of the Tooth',
    img: '/images/kandy.jpg',
  },
  {
    id: 6,
    name: 'Kandy',
    location: 'Sri Lanka',
    desc: 'Temple of the Tooth',
    img: '/images/kandy.jpg',
  },
  {
    id: 7,
    name: 'Kandy',
    location: 'Sri Lanka',
    desc: 'Temple of the Tooth',
    img: '/images/kandy.jpg',
  },
  {
    id: 8,
    name: 'Kandy',
    location: 'Sri Lanka',
    desc: 'Temple of the Tooth',
    img: '/images/kandy.jpg',
  },
  {
    id: 9,
    name: 'Kandy',
    location: 'Sri Lanka',
    desc: 'Temple of the Tooth',
    img: '/images/kandy.jpg',
  },
  {
    id: 10,
    name: 'Kandy',
    location: 'Sri Lanka',
    desc: 'Temple of the Tooth',
    img: '/images/kandy.jpg',
  },
];

export default function TouristPlacesPage() {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user') || 'null');

  if (!user) {
    navigate('/');
    return null;
  }

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/login');
  };

  return (
    <div className="places-container">
      <div className="top-bar">
        <h2>Welcome, {user.name}</h2>
        <button className="logout-btn" onClick={handleLogout}>
          Logout
        </button>
      </div>

      <div className="places-grid">
        {samplePlaces.map((place) => (
          <div className="place-card" key={place.id}>
            <img src={place.img} alt={place.name} className="place-image" />
            <h3>{place.name}</h3>
            <p className="place-location">{place.location}</p>
            <p className="place-desc">{place.desc}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
