import React from 'react';
import { useNavigate } from 'react-router-dom';

const samplePlaces = [
  { id:1, name:'Sigiriya', location:'Sri Lanka', desc:'Ancient rock fortress' },
  { id:2, name:'Ella', location:'Sri Lanka', desc:'Scenic hill country' },
  { id:3, name:'Galle', location:'Sri Lanka', desc:'Dutch fort city' },
  { id:4, name:'Nuwara Eliya', location:'Sri Lanka', desc:'Tea plantations' }
];

export default function TouristPlacesPage() {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user') || 'null');

  if (!user) {
    navigate('/');
    return null;
  }

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/');
  };

  return (
    <div style={{padding:20}}>
      <div style={{display:'flex', justifyContent:'space-between', alignItems:'center'}}>
        <h2>Welcome, {user.name}</h2>
        <button className="btn" onClick={handleLogout} style={{width:120}}>Logout</button>
      </div>
      <div className="places-grid" style={{marginTop:20}}>
        {samplePlaces.map(p => (
          <div className="card" key={p.id}>
            <h3>{p.name}</h3>
            <p style={{fontSize:14, color:'#555'}}>{p.location}</p>
            <p style={{fontSize:13}}>{p.desc}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
