import React, { useState } from 'react';
import axios from 'axios';

export default function ContactPage() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [status, setStatus] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus('');
    setError('');

    try {
      // Replace with your backend API URL for contact form
      await axios.post('http://localhost:5000/api/contact', { name, email, message });
      setStatus('Your message has been sent successfully!');
      setName('');
      setEmail('');
      setMessage('');
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to send message');
    }
  };

  return (
    <div className="page-root" style={{ padding: '60px 6%' }}>
      <div className="contact-card">
        <h2 className="login-title">Contact Us</h2>

        <form onSubmit={handleSubmit}>
          <label className="label">Name</label>
          <input
            className="login-input"
            placeholder="Enter your name"
            value={name}
            onChange={e => setName(e.target.value)}
            required
          />

          <label className="label">Email</label>
          <input
            className="login-input"
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={e => setEmail(e.target.value)}
            required
          />

          <label className="label">Message</label>
          <textarea
            className="login-input"
            placeholder="Write your message here"
            value={message}
            onChange={e => setMessage(e.target.value)}
            rows={5}
            style={{ resize: 'vertical' }}
            required
          />

          {status && <div className="success-text">{status}</div>}
          {error && <div className="error-text">{error}</div>}

          <button type="submit" className="login-btn" style={{ marginTop: '24px' }}>Send</button>
        </form>
      </div>
    </div>
  );
}
