import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/LoginPage.css';

export default function LoginPage() {
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    if (!name.trim() || !password.trim()) {
      setError('Please enter both username and password');
      return;
    }

    try {
      setError('');
      const res = await axios.post('http://localhost:5000/api/login', { name, password });
      localStorage.setItem('user', JSON.stringify(res.data.user));
      navigate('/places');
    } catch (err) {
      setError(err.response?.data?.message || 'Login failed');
    }
  };

  return (
    <div className="page-root">
      <header className="topbar">
        <div className="brand">Globe Hub</div>
      </header>

      <main className="hero-wrap">
        <aside className="login-column">
          <div className="login-card">
            <div className="login-title">Login Here</div>

            <form onSubmit={handleLogin} className="login-form">
              <label className="label">Enter Username</label>
              <input
                className="login-input"
                placeholder="Username"
                value={name}
                onChange={(e) => setName(e.target.value)}
                autoComplete="username"
              />

              <label className="label">Enter Password</label>
              <input
                type="password"
                className="login-input"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                autoComplete="current-password"
              />

              {error && <div className="error-text" style={{ color: 'red', marginTop: '0.5rem' }}>{error}</div>}

              <button className="login-btn" type="submit" style={{ marginTop: '1rem' }}>
                Login
              </button>
            </form>

            <div className="signup-row" style={{ marginTop: '1rem' }}>
              <div>Don't have an account?</div>
              <Link to="/register" className="signup-link" style={{ marginLeft: '0.5rem' }}>
                Sign up here
              </Link>
            </div>
          </div>
        </aside>
      </main>
    </div>
  );
}
